devices.semantics:
# Transport an object

# Standard namespaces
@prefix : <ex#>.
@prefix local: <local#>.
@prefix ex: <http://example.org/#>.
@prefix http: <http://www.w3.org/2011/http#>.
@prefix st: <http://purl.org/restdesc/states#>.
@prefix log: <http://www.w3.org/2000/10/swap/log#>.
@prefix owl: <http://www.w3.org/2002/07/owl#>.

# Domain-specific namespaces
@prefix dbpedia: <http://dbpedia.org/resource/>.

:TheMobileRobot a ex:Carrier; ex:locatedIn :Location1.

{
	:TheMobileRobot ex:hasLoaded ?object; ex:locatedIn ?location.
	?object a ex:PhysicalObject.
	?location a ex:Room; ex:id ?sourceID.
	?newLocation a ex:Room; ex:id ?destinationID.
}
=>
{
	_:request http:methodName "PUT";
		http:requestURI ("http://mobileRobot.local/destination");
		http:reqBody ?destinationID.

	:TheMobileRobot ex:hasLoaded ?object; ex:locatedIn ?newLocation.
}.

{
	:TheMobileRobot ex:hasLoaded ?object; ex:locatedIn ?location.
}
=>
{
	_:request http:methodName "PUT";
		http:requestURI ("http://mobileRobot.local/drop");
		http:reqBody ?object.

	?object ex:locatedIn ?location.
}.

