devices.semantics:
@prefix : <ex#>.
@prefix local: <local#>.
@prefix dbpedia: <http://dbpedia.org/resource/>.
@prefix geonames: <http://www.geonames.org/ontology#>.
@prefix http: <http://www.w3.org/2011/http#>.
@prefix log: <http://www.w3.org/2000/10/swap/log#>.
@prefix owl: <http://www.w3.org/2002/07/owl#>.
@prefix schema: <http://schema.org/>.
@prefix st: <http://purl.org/restdesc/states#>.
@prefix ex: <http://example.org/#>.

{
	?thermostat a dbpedia:Thermostat;
		geonames:locatedIn ?thermostatPlace.

	?state a st:State;
		log:includes { ?thermostatPlace ex:hasTemperature ?old. }.

	?new a dbpedia:Temperature;
		ex:hasValue ?tempValue;
		ex:hasUnit "Celsius".
}
=>
{
	_:request http:methodName "PUT";
		http:requestURI (?thermostat "/ac/target");
		http:reqBody ?tempValue.

	[ a st:StateChange;
		st:added { ?thermostatPlace ex:hasTemperature ?new. };
		st:removed { ?thermostatPlace ex:hasTemperature ?old. };
		st:parent ?state ].
}.

