		ex:hasUnit "Fahrenheit";
		ex:derivedFrom ?degreesCelsius.

	_:request http:methodName "POST";
		http:requestURI (?url  "/cel2degf");
		http:reqBody ?celsiusValue;
		http:resp ?fahrenheitValue.
}.

